import React, {useState} from "react";


export default function TextForm(props) {

const handleUpClick = ()=>{
  // console.log("Uppercase was clicked" + text)
  let newText = text.toUpperCase();
  setText(newText)
}

const handleonChange = (event)=>{
  // console.log("On Change")
  setText(event.target.value);
}

  const [text, setText] = useState('Enter text here');
  //ext = "new text"; // wrong way to change the state
  //setText = ("new text"); // Correct way to change the state
  return (
    <>
    <div className="container">
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea
          className="form-control" value={text}
          onChange={handleonChange} id="myBox"
          rows="3"
        ></textarea>
      </div>

      <button className="btn btn-primary" onClick={handleUpClick}>Convert to Upercase</button>
    </div>
    <div className="container my-3">
      <h1>Your text Summary</h1>
      <p>{text.split} words and {text.length} characters</p>
    </div>
    </>
  );
}
