import React, {useState} from "react";


export default function TextForm(props) {

const handleUpClick = ()=>{
  // console.log("Uppercase was clicked" + text)
  let newText = text.toUpperCase();
  setText(newText)
}


const handleLoClick = ()=>{
  // console.log("Uppercase was clicked" + text)
  let newText = text.toLowerCase();
  setText(newText)
}

const handleonChange = (event)=>{
  // console.log("On Change")
  setText(event.target.value);
}

  const [text, setText] = useState('');
  //ext = "new text"; // wrong way to change the state
  //setText = ("new text"); // Correct way to change the state
  return (
    <>
    <div className="container">
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea
          className="form-control" value={text}
          onChange={handleonChange} id="myBox"
          rows="3"
        ></textarea>
      </div>

      <button className="btn btn-primary mx-1" onClick={handleUpClick}>Convert to Upercase</button>
      <button className="btn btn-primary mx-1" onClick={handleLoClick}>Convert to Lowercase</button>
      <button className="btn btn-primary mx-1" onClick={HandleClear}>Convert to Lowercase</button>
    </div>
    <div className="container my-3">
      <h1>Your text Summary</h1>
      <p>{text.split(" ").lenght} words and {text.length} characters</p>
      <p>{0.008 * text.split(" ").lenght} Minutes read</p>
      <h2>Preview</h2>
      <p>{text}</p>
    </div>
    </>
  );
}
